function PrintByteSlice(name, slice) {
	var s = "" + name + " is : [";
	for (var index = 0; index < slice.length - 1; index++) {
		s += "" + slice[index] + ",";
	}
	s += "" + slice[slice.length - 1] + "]";

	console.log(s + "\n");
}

