function PrintByteSlice(name, slice) {
	var s = "" + name + " is : [";
	for (var index = 0; index < slice.len - 1; index++) {
		s += "" + slice[index] + ",";
	}
	s += "" + slice[slice.len - 1] + "]";

	console.log(s + "\n");
}

