function testSimpleFunc() {

	var max = function(a, b) {
		if (a > b) {
			return a;
		}
		return b;
	};

	var x = 3;
	var y = 4;
	var z = 5;

	var max_xy = max(x, y);
	var max_xz = max(x, z);

	alert("max(" + x + ", " + y + ") = " + max_xy + "\n");
	alert("max(" + x + ", " + z + ") = " + max_xz + "\n");
	alert("max(" + y + ", " + z + ") = " + max(y, z) + "\n");
}























function testByValue() {

	var add = function(v) {
		v = v + 1;
		return v;
	};

	var x = 3;
	alert("x =  " + x + "\n");

	var x1 = add(x);
	alert("x+1 =  " + x1 + "\n");
	alert("x =  " + x + "\n");
}

function testByReference() {
	var add = function(v) {
		v[0] = v[0] + 1;
		return v[0];
	};

	var x = 3;
	alert("x =  " + x + "\n");

	var x1 = add(x=[x]);
	alert("x+1 =  " + x1 + "\n");
	alert("x =  " + x + "\n");

	x1 = add(x);
	alert("x+1 =  " + x1 + "\n");
	alert("x =  " + x + "\n");
}

